	/*Scrollbar fix for chrome*/
	body::-webkit-scrollbar-thumb
	{
		height: 150px;
		background: hsl(0, 0%, 56%);
	}
	body::-webkit-scrollbar-thumb:hover
	{
		background: hsl(0, 0%, 11%);
	}
	body::-webkit-scrollbar
	{
		width: 10px;
		background: hsl(0, 0%, 26%);
	}

